import React, { useEffect } from "react";
import { connect } from "react-redux";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";
import Chip from "@material-ui/core/Chip";
import Grid from "@material-ui/core/Grid";
import IconButton from "@material-ui/core/IconButton";
import Hidden from "@material-ui/core/Hidden";

//icons
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import LocationOnOutlinedIcon from "@material-ui/icons/LocationOnOutlined";
import PhoneOutlinedIcon from "@material-ui/icons/PhoneOutlined";
import ErrorOutlineOutlinedIcon from "@material-ui/icons/ErrorOutlineOutlined";
import BookmarkIcon from '@material-ui/icons/Bookmark';
import BookmarkBorderOutlinedIcon from "@material-ui/icons/BookmarkBorderOutlined";
import ShareOutlinedIcon from "@material-ui/icons/ShareOutlined";
import FavoriteIcon from "@material-ui/icons/Favorite";
import FavoriteBorderOutlinedIcon from "@material-ui/icons/FavoriteBorderOutlined";

import PropTypes from "prop-types";

import { changeDocLikeStatus } from "../../redux/actions/userActions";
import { updateDoctorLikes } from "../../redux/actions/dataActions";

//images
import docImg from "../../img/results/docAlex.png";

//components
import CovidAlert from "../Alert";
import DocTags from "./DocTags";

const useStyles = makeStyles((theme) => ({
  covidBox: {
    marginTop: 64,
    [theme.breakpoints.down("sm")]: {
      marginTop: 20,
      marginLeft: 20,
      marginRight: 20,
    },
  },

  //return to doctors button
  returnBox: {
    marginLeft: 50,
    [theme.breakpoints.down("sm")]: {
      marginLeft: 20,
    },
  },

  //doctor image
  img: {
    width: "100%",
    height: "100%",
    objectFit: "contain",
  },

  profileGrid: {
    [theme.breakpoints.down("xs")]: {
      marginLeft: 20,
      marginRight: 20,
    },
  },

  line: {
    [theme.breakpoints.down("xs")]: {
      border: "5px solid rgba(0, 0, 0, 0.05)",
    },
  },
}));

//for Doc image + basic info + likes
//edit tags section is imported from DocTags.js

function DocInfo(props) {
  const classes = useStyles();

  const backToRes = () => {
    if (props.history != null) {
      if (props.backTo == "resultsPage") {
        props.history.push("/results");
      } else if (props.backTo == "profilePage") {
        props.history.push("/hospprofile");
      }
    }
  };


  const authenticated = props.authenticated;

  const [renderCount, setRenderCount] = React.useState(0);

  const [likeSaveInfo, setState] = React.useState({
    // like functionality
    hasLiked: false,
    likedList: [],
    numLikes: 0,

    // save functionality
    hasSaved: false,
    savedList: []
  });

  // only get called once in the first render
  useEffect(() => {
    if (renderCount == 0) {
      return displayStoredData();
    }
  });

  const displayStoredData = () => {
    getStoredData()
      .then((res) => {
        // like
        let listOfLikes;
        let liked = false;

        // if the user has never liked any doctors
        if (!res[1].likeHistory) {
          listOfLikes = [];
        } else {
          if (res[1].likeHistory.doctors) {
            listOfLikes = res[1].likeHistory.doctors;
            // if the user has liked this particular doctor before
            const index = res[1].likeHistory.doctors.findIndex(doctor => doctor.username == "verify")
            if (index != -1) {
              liked = true;
            }
          } else {
            listOfLikes = [];
          }
        }

        // save
        let listOfSaves;
        let saved = false;

        // if the user has never saved any doctors
        if (!res[1].saved) {
          listOfSaves = [];
        } else {
          if (res[1].saved.doctors) {
            listOfSaves = res[1].saved.doctors;
            // if the user has saved this particular doctor before
            const index = res[1].saved.doctors.findIndex(doctor => doctor.username == "verify")
            if (index != -1) {
              saved = true;
            }
          } else {
            listOfSaves = [];
          }
        }

        setState({
          hasLiked: liked,
          likedList: listOfLikes,
          numLikes: res[0].likes ? res[0].likes : 0,

          hasSaved: saved,
          savedList: listOfSaves,
        });

        setRenderCount(1);
      }).catch((error) => {
        console.error(error);
      })
  }

  // wait for returned props from firebase to be ready
  let getStoredData = async () => {
    let [storedSearchInfo, userStoredCredentials] =
      await Promise.all([props.searchInfo, props.storedCredentials]);
    return [storedSearchInfo, userStoredCredentials];
  }



  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~LIKE FUNCTIONALITY~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

  // when the like button is pressed
  const toggleLikeUnlike = () => {
    // the user has liked this doctor before
    if (likeSaveInfo.hasLiked) {
      let likedListCopy = likeSaveInfo.likedList;
      let index = likedListCopy.findIndex(doctor => doctor.username == "verify");
      // remove this doctor from the user like list
      likedListCopy.splice(index, 1);
      setState(prevState => ({
        ...likeSaveInfo,
        likedList: likedListCopy,
        // change to unlike
        hasLiked: false,
        numLikes: prevState.numLikes - 1
      }))
    } else {
      // the newly liked doctor's information to be added to the user's liked doctor list
      const newDocInfo = {
        hospital: "Pantai Hospital Kuala Lumpur",
        imgSrc: "",
        languages: ["English", "Mandarin"],
        likes: likeSaveInfo.numLikes,
        name: "Alex Leow",
        specialty: "Gastroenterology",
        type: "Private",
        username: "verify"
      };

      setState(prevState => ({
        // add to the list if the list contains other doctors, otherwise use this doctor to start a list
        likedList: (likeSaveInfo.likedList.length == 0) ? [newDocInfo] : [prevState.likedList, newDocInfo],
        hasLiked: true,
        numLikes: prevState.numLikes + 1
      }))
    }
  }

  // triggered if the like button is pressed (which sets state)
  useEffect(() => {
    if (renderCount == 1) {
      return toggleLike();
    }
  }, [likeSaveInfo.hasLiked, likeSaveInfo.likedList]);


  const toggleLike = () => {
    waitLikeUpdate()
      .then((res) => {
        props.changeDocLikeStatus(res[0]);
        const updateInfo = {
          specialty: "Gastroenterology",
          hospital: "Pantai Hospital Kuala Lumpur",
          username: "verify",
          likes: res[1]
        }
        props.updateDoctorLikes(updateInfo)
      }).catch((error) => {
        console.error(error);
      });
  }

  let waitLikeUpdate = async () => {
    let [likedList, likes] =
      await Promise.all([likeSaveInfo.likedList, likeSaveInfo.numLikes]);
    return [likedList, likes];
  }


  // if the user has liked this doctor before: filled heart, otherwise outlined heart
  const LikeIcon = (likeSaveInfo.hasLiked) ? FavoriteIcon : FavoriteBorderOutlinedIcon;





  // ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~SAVE FUNCTIONALITY~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~

  // when the save button is pressed
  const toggleSaveUnsave = () => {
    // the user has saved this doctor before
    if (likeSaveInfo.hasSaved) {
      let savedListCopy = likeSaveInfo.savedList;
      let index = savedListCopy.findIndex(doctor => doctor.username == "verify");
      // remove this doctor from the user saved list
      savedListCopy.splice(index, 1);

      setState({
        ...likeSaveInfo,
        savedList: savedListCopy,
        hasSaved: false,
      })
    } else {
      // the newly saved doctor's information to be added to the user's saved doctor list
      const newDocInfo = {
        hospital: "Pantai Hospital Kuala Lumpur",
        imgSrc: "",
        languages: ["English", "Mandarin"],
        likes: likeSaveInfo.numLikes,
        name: "Alex Leow",
        specialty: "Gastroenterology",
        type: "Private",
        username: "verify"
      };

      setState(prevState => ({
        // add to the list if the list contains other doctors, otherwise use this doctor to start a list
        ...likeSaveInfo,
        savedList: (likeSaveInfo.savedList.length == 0) ? [newDocInfo] : [prevState.savedList, newDocInfo],
        hasSaved: true,
      }))
    }
  }

  // triggered if the save button is pressed (which sets state)
  useEffect(() => {
    if (renderCount == 1) {
      return toggleSave();
    }
  }, [likeSaveInfo.hasSaved, likeSaveInfo.savedList]);


  const toggleSave = () => {
    waitSaveUpdate()
      .then((res) => {
        props.changeDocSaveStatus(res);
      }).catch((error) => {
        console.error(error);
      });
  }

  let waitSaveUpdate = async () => {
    let savedList = await likeSaveInfo.savedList;
    return savedList;
  }


  // if the user has liked this doctor before: filled heart, otherwise outlined heart
  const SaveIcon = (likeSaveInfo.hasSaved) ? BookmarkIcon : BookmarkBorderOutlinedIcon;







  return (
    <div>
      <div className={classes.covidBox}>
        <CovidAlert />
      </div>

      {/* For 'return to doctors' button (return to hospital depending on user) */}
      <Box display="flex" mt={3} mb={3} className={classes.returnBox}>
        <Button
          style={{ fontSize: 16, textTransform: "none" }}
          color="primary"
          onClick={backToRes}
          startIcon={<ArrowBackIosIcon />}
        >
          Return to Doctors
        </Button>
      </Box>

      {/* doctor like icon */}
      <Hidden smUp>
        <Grid container display="flex" justifyContent="center">
          <Grid item xs={3}></Grid>
          <Grid item xs={6} align="center">
            {/* doctor image */}
            <div style={{ width: 150, height: 180 }}>
              <img className={classes.img} src={docImg} alt="docimg" />
            </div>
          </Grid>
          {/* Like icon + number of likes */}
          <Grid item xs={3}>
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
            >
              {/* iconiconButton , logic, button
              <FavoriteBorderOutlinedIcon> <FavoriteIcon>, likeCount
              */}
              {authenticated ? (
                <IconButton onClick={toggleLikeUnlike}>
                  <LikeIcon style={{ color: "red" }} />
                </IconButton>
              ) : (
                  <FavoriteIcon style={{ color: "red" }} />
                )}
              {/* like count */}
              <Typography variant="body2" color="primary">
                {likeSaveInfo.numLikes.toLocaleString(navigator.language, { minimumFractionDigits: 0 })}
              </Typography>
            </Box>
          </Grid>
        </Grid>
      </Hidden>

      <Grid container spacing={0}>
        <Grid item xs={12} sm={5} md={4}>
          <Box
            display="flex"
            flexDirection="column"
            justifyContent="center"
            alignItems="center"
          >
            {/* doctor */}
            <Hidden xsDown>
              {/* doctor image */}
              <div style={{ width: 200, height: 250 }}>
                <img className={classes.img} src={docImg} alt="docimg" />
              </div>
            </Hidden>

            {/* doctor's name  */}
            <Hidden smUp>
              <Typography variant="h5" color="primary" style={{ margin: 20 }}>
                {"Dr." + props.targetDoc["DocName"]}
              </Typography>
            </Hidden>

            <Box display="flex" mt={2}>
              {/* report button */}
              <Button
                startIcon={<ErrorOutlineOutlinedIcon />}
                style={{ textTransform: "none" }}
                color="primary"
              >
                Report
              </Button>
              {/* share button */}
              <Button
                startIcon={<ShareOutlinedIcon />}
                style={{ textTransform: "none" }}
                color="primary"
              >
                Share
              </Button>
              {/* save button */}
              <Button
                startIcon={<SaveIcon />}
                style={{ textTransform: "none" }}
                color="primary"
                onClick={toggelSaveUnsave}
              >
                Save
              </Button>
            </Box>
          </Box>
        </Grid>

        <Grid item xs={12} sm={6} md={6}>
          {/* doctor name  */}
          <Hidden xsDown>
            <Typography variant="h5" color="primary">
              {"Dr." + props.targetDoc["DocName"]}
            </Typography>
          </Hidden>
          <br></br>

          {/* divider line */}
          <Hidden smUp>
            <hr className={classes.line}></hr>
          </Hidden>

          {/* Doctor basic info */}
          <Box className={classes.profileGrid} mt={3} mb={3}>
            <Typography variant="body1" color="textPrimary">
              <strong>Specialty: </strong>{" "}
              <span>{props.targetDoc["Specialty"]} </span>
            </Typography>

            <Typography variant="body1" color="textPrimary">
              <strong>Years of Practice: </strong>{" "}
              <span>{props.targetDoc["YearsofPractice"] + " years"} </span>
            </Typography>

            <Typography variant="body1" color="textPrimary">
              <strong>Hospital: </strong>
              <span>{props.targetDoc["Hospital"]}</span>
              {/* private tag */}
              <Chip
                style={{ marginLeft: 10 }}
                color="primary"
                size="small"
                label={props.targetDoc["Type"]}
              ></Chip>
            </Typography>
            <br></br>

            <Box display="flex" flexDirection="row" mt={1}>
              <LocationOnOutlinedIcon style={{ marginRight: 10 }} />
              <Typography variant="body1" color="textPrimary">
                <span>{props.targetDoc["Address"]}</span>
              </Typography>
            </Box>
            <Box display="flex" flexDirection="row" mt={1}>
              <PhoneOutlinedIcon style={{ marginRight: 10 }} />
              <Typography variant="body1" color="textPrimary">
                <span>{props.targetDoc["Phone"]}</span>
              </Typography>
            </Box>
          </Box>
          {/* edit Tag component (imported from Tag.js) */}
          <DocTags tagInfo={props.targetDoc["Tags"]} />
        </Grid>

        <Grid item xs={12} sm={1} md={2}>
          {/* like icon */}
          <Hidden xsDown>
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
            >
              {/* iconiconButton , logic, button
              <FavoriteBorderOutlinedIcon> <FavoriteIcon>, likeCount
              */}
              {authenticated ? (
                <IconButton onClick={toggleLikeUnlike}>
                  <LikeIcon style={{ color: "red" }} />
                </IconButton>
              ) : (
                  <FavoriteIcon style={{ color: "red" }} />
                )}
              <Typography variant="body2" color="primary">
                {likeSaveInfo.numLikes.toLocaleString(navigator.language, { minimumFractionDigits: 0 })}
              </Typography>
            </Box>
          </Hidden>
        </Grid>
      </Grid>
    </div>
  );
}

DocInfo.propTypes = {
  changeDocLikeStatus: PropTypes.func.isRequired,
  updateDoctorLikes: PropTypes.func.isRequired,

  changeDocSaveStatus: PropTypes.func.isRequired,

};

const mapStateToProps = (state) => ({
  authenticated: state.user.authenticated,
  storedCredentials: state.user.credentials,
  searchInfo: state.data.searchInfo
});

const mapActionsToProps = {
  changeDocLikeStatus,
  updateDoctorLikes,

  changeDocSaveStatus
};

export default connect(mapStateToProps, mapActionsToProps)(DocInfo);
